package foreachloop;

import java.util.ArrayList;

public class ForEachExampleCollection {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
ArrayList<String> a = new ArrayList<String>();
a.add("Red");
a.add("Yellow");
a.add("Blue");
for(String b:a) {
	System.out.println(b);
}
	}

}
